import UIKit

class FollowIncomingWireframe: BaseWireframe {
    
    // TODO: Use tuples for extras if you have more than once parameters
    func show(transitionType: WireframeTransitionType, delegate: FollowIncomingPresenterDelegateProtocol? = nil, extras: Any? = nil) {
        // TODO: Choose related storyboard
        let viewController: FollowIncomingViewController = UIStoryboard.Follow.instantiateViewController()
        
        // TODO: Inteactor Dependencies
        let interactor = FollowIncomingInteractor(networkService: NetworkService.sharedInstance)
        
        let presenter = FollowIncomingPresenter(viewController: viewController, interactor: interactor, delegate: delegate, extras: extras)
        
        viewController.presenter = presenter
        interactor.presenter = presenter
        
        changeView(transationType: transitionType, viewController: viewController)
    }
    
}
